CREATE TABLE IF NOT EXISTS product_colors (
	id SERIAL PRIMARY KEY,
	color TEXT
);

CREATE TABLE IF NOT EXISTS product_sizes (
	id SERIAL PRIMARY KEY,
	size varchar
);

CREATE TABLE IF NOT EXISTS product_categories (
	id SERIAL PRIMARY KEY,
	categoryName TEXT NOT NULL
);

CREATE TABLE IF NOT EXISTS product_images (
	id SERIAL PRIMARY KEY,
	thumbnail TEXT NOT NULL DEFAULT '/public/assets/default-thumbnail.jpg',
	image TEXT NOT NULL DEFAULT '/public/assets/default-image.jpg'
);

CREATE TABLE IF NOT EXISTS products (
	id SERIAL PRIMARY KEY,
	productName TEXT NOT NULL,
	productDescription TEXT,
	material TEXT NOT NULL,
	imageID INT NOT NULL,
	categoryID INT NOT NULL,
	CONSTRAINT fk_category FOREIGN KEY(categoryID) REFERENCES product_categories(id),
	CONSTRAINT fk_image FOREIGN KEY(imageID) REFERENCES product_images(id)
);

CREATE TABLE IF NOT EXISTS product_options (
	productID INT NOT NULL REFERENCES products(id),
	sizeID INT NOT NULL REFERENCES product_sizes(id),
	colorID INT NOT NULL REFERENCES product_colors(id),
	quantity INT DEFAULT 0,
	price float DEFAULT 0.00,
	CONSTRAINT fk_product_option_key PRIMARY KEY(productID, sizeID, colorID)
);

